descriptor.controllerScript:
self.onInit = function() {
    self.ctx.gauge = new TbCanvasDigitalGauge(self.ctx, 'digitalGauge');    
}

self.onDataUpdated = function() {
    self.ctx.gauge.update();
}

self.onResize = function() {
    self.ctx.gauge.resize();
}

self.typeParameters = function() {
    return {
        maxDatasources: 1,
        maxDataKeys: 1,
        singleEntity: true
    };
}

self.onMobileModeChanged = function() {
    self.ctx.gauge.mobileModeChanged();
}

self.onDestroy = function() {
    self.ctx.gauge.destroy();
}

