descriptor.templateCss:
.legend {
    font-size: 13px;
    line-height: 10px;
}

.legend table { 
    border-spacing: 0px;
    border-collapse: separate;
}

.pie-label {
    font-size: 12px;
    font-family: 'Roboto';
    font-weight: bold;
    text-align: center;
    padding: 2px;
    color: white;
}

descriptor.controllerScript:
self.onInit = function() {
    self.ctx.flot = new TbFlot(self.ctx, 'pie');    
}

self.onDataUpdated = function() {
    self.ctx.flot.update();
}

self.onResize = function() {
    self.ctx.flot.resize();
}

self.onEditModeChanged = function() {
    self.ctx.flot.checkMouseEvents();
}

self.onDestroy = function() {
    self.ctx.flot.destroy();
}
self.actionSources = function() {
    return {
        'sliceClick': {
            name: 'widget-action.pie-slice-click',
            multiple: false
        }
    };
}

