};

self.onDataUpdated = function() {
    self.ctx.$scope.rangeChartWidget.onDataUpdated();
}

self.typeParameters = function() {
    return {
        maxDatasources: 1,
        maxDataKeys: 1,
        singleEntity: true,
        previewWidth: '80%',
        embedTitlePanel: true,
        hasAdditionalLatestDataKeys: false,
        defaultDataKeysFunction: function() {
            return [{ name: 'temperature', label: 'Temperature', type: 'timeseries' }];
        }
    };
}

